using System;
using System.IO;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;

class Program
{
    static void Main(string[] args)
    {
        // Read JSON data from a file
        var json = File.ReadAllText("data.json");
        var data = JObject.Parse(json);

        // Manipulate the data
        // Example: Change a value, add a new field, delete a field
        if (data.ContainsKey("field_to_change"))
        {
            data["field_to_change"] = "new_value";
        }

        data["new_field"] = "new_value";

        if (data.ContainsKey("field_to_delete"))
        {
            data.Remove("field_to_delete");
        }

        // Write the manipulated data back to the file
        File.WriteAllText("data.json", data.ToString(Formatting.Indented));
    }
}
